package hello_world;

public class SumOfTenNumbers {

	public static void main(String[] args) {
		int count=0;
		for(int i=1;i<=10;i++) {
			System.out.println(i);
			count+=i;
			
			
		}
		System.out.println();
		System.out.println("the sum is:"+count);
		// TODO Auto-generated method stub

	}

}
